import React, { Component } from "react";
import '../styles/App.css';

import Youtube from '../apis/Youtube';
import SearchBar from './SearchBar';
import VideoList from './VideoList';
import videoDetail from './VideoDetail';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      videos: [],
      selecedVideo: null,
    }
  }

  handleSubmit = async (term) => {
    const res = await Youtube.get('/search', {
      params: { q: term }
    });
    //debugger;
    this.setState({
      videos: res.data.items
    });
  }

  // videoItem에서 전달된 video 객체를 selectedVideo로 할당, selectedVideo는 videoDetail로 전달
  handleVideoSelect = (video) => {
    this.setState({ selecedVideo: video });
    console.log('▶▶▶', this.state.selecedVideo);
  }

  render() {
    console.info('App render');
    return (
      <div className='App container' style={{ marginTop: '10px'}}>
        <div className='row'>
          <div className='col'>
            <SearchBar handleFormSubmit={this.handleSubmit}></SearchBar>
            <div className='row'>
              <div className='col-8' style={{ backgroundColor: 'gray' }}>
                <videoDetail/>
              </div>
              <div className='col-4'>
                <VideoList 
                  videos={this.state.videos}
                  handleVideoSelect={this.handleVideoSelect}>
                </VideoList>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
